import React from 'react';
import { Button, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
class MyClassComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            colour: "danger",
            disabled: true,
            name: `${this.props.row}${this.props.num}`,
            button: null
        };

        this.seatPurchaseChild = this.seatPurchaseChild.bind(this);
        this.seatPurchaseAdult = this.seatPurchaseAdult.bind(this);
        this.tempDis = this.tempDis.bind(this);

    }
    componentDidMount() {
        if (this.props.data !== "0") {
            this.setState({
                colour: "danger",
                disabled: true, 
                button: <Dropdown size="sm" direction="right" isOpen={this.state.btnDropright} toggle={() => { this.setState({ btnDropright: !this.state.btnDropright }); }}><DropdownToggle disabled={this.state.disabled} color={this.state.colour}> {this.state.name}</DropdownToggle> <DropdownMenu><DropdownItem onClick={this.seatPurchaseChild}>Child</DropdownItem><DropdownItem onClick={this.seatPurchaseAdult}>Adult</DropdownItem></DropdownMenu></Dropdown>
            })
        }
        else {
            this.setState({
                colour: "success",
                disabled: false,
            })
        }
    }
    seatPurchaseChild() {
        let a = `${this.props.row}${this.props.num}`;
            this.setState({ colour: "primary" })
            this.setState({button:<Button size="sm" color={this.state.colour} onClick={this.seatPurchasecancel}></Button>})
            this.props.chi(a);

    }
    seatPurchaseAdult(){
        let a = `${this.props.row}${this.props.num}`;
            this.setState({ colour: "primary" })
            this.setState({button:<Button size="sm" color={this.state.colour} onClick={this.seatPurchasecancel}></Button>})
            this.props.adult(a);
    }
    render() {
        return (
            <>
            {this.state.button}
            </>
        );
    }

};

export default MyClassComponent;
