import React, { createRef } from 'react';

class FindUs extends React.Component {
    googleMapRef = createRef()
    
   createGoogleMap = () => {
       new window.google.maps.Map(this.googleMapRef.current, {
           zoom: 16,
           center: {
               lat: 53.474005,
               lng: -2.2879854
           },
           disableDefaultUI: true
       })
   }

   createMarker = () => {
       new window.google.maps.Marker({
           position: {lat: 53.473676, lng: -2.2881236},
           map: this.googleMapRef
       })
   }

   componentDidMount() {
       const googleScript = document.createElement('script')

       googleScript.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyCDQ3GLDoRqdKEgq8tb7Drm8BJ9BN2cWwM&callback=initMap`
       window.document.body.appendChild(googleScript)

       googleScript.addEventListener('load', {
           googleMap : this.createGoogleMap()
       })
   }

   render() {
       return ( 
           <div class="container">
             <br></br>
               <div class="row">
                <div class="col">
                    <div id="google-map" ref={this.googleMapRef} style={{width:"600px", height:"500px"}}/>  
                </div>
               </div>
           </div>
        )
   }
}
export default FindUs;
