import React from 'react';
import { Button, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
class MyClassComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            colour: "danger",
            disabled: true,
            name: `${this.props.row}${this.props.num}`,
            active: false,
            drop: <><Dropdown size="sm" direction="right" isOpen={this.state.btnDropright} onClick={this.tempDis} toggle={() => { this.setState({ btnDropright: !this.state.btnDropright }); }}>
                <DropdownToggle disabled={this.state.disabled} color={this.state.colour}>
                {this.state.name}
                </DropdownToggle>
                {this.state.button}
                </Dropdown></>,
                button: <><DropdownMenu disabled={this.state.disabled}>
                <DropdownItem onClick={this.seatPurchaseChild}>Child</DropdownItem>
                <DropdownItem onClick={this.seatPurchaseAdult}>Adult</DropdownItem>
            </DropdownMenu></>
        };

        this.seatPurchaseChild = this.seatPurchaseChild.bind(this);
        this.seatPurchaseAdult = this.seatPurchaseAdult.bind(this);
        this.tempDis = this.tempDis.bind(this);

    }
    componentDidMount() {
        if (this.props.data !== "0") {
            this.setState({
                colour: "danger",
                disabled: true, 
            })
        }
        else {
            this.setState({
                colour: "success",
                disabled: false,
                active: false,
                
            })
        }
    }
    seatPurchaseChild() {
        this.setState({ colour: "primary" })
        this.setState({active:false})
        this.setState({button: null})
            this.props.chi(this.state.name);

        
    }
    seatPurchaseAdult(){
        
            this.setState({ colour: "primary" })
            this.setState({active:false})
            this.setState({button: null})
            this.props.adult(this.state.name);
    }
    tempDis() {
            this.setState({color:"success"})
            this.props.delete()
        
    }
    render() {
        return (
           <>
           {this.state.drop}
           </>
        );
    }

};

export default MyClassComponent;
