import React from 'react';
import { Button} from 'reactstrap';
class MyClassComponent extends React.Component{
    constructor(props) {
        super(props);
        if(this.props.data !== "0"){
            this.state = {
                colour: "danger"
            };
        }
        else{
            this.state = {
                colour: "success"
            };
        }
        this.seatPurchase = this.seatPurchase.bind(this);

    }
    seatPurchase(){
        let a = `${this.props.row}${this.props.num}`;
        if(this.state.colour === "success"){
        this.setState({colour: "primary"})
        this.props.pay(a);
        }else{
        this.setState({colour: "success"})
        }
    }
    render() {
        return (
        <Button size="sm" color={this.state.colour} onClick={this.seatPurchase}>{this.props.row}{this.props.num}</Button>
        );
    }
    
};

export default MyClassComponent;
