import React from 'react';
import { Button, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
class MyClassComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            colour: "danger",
            disabled: true,
            name: `${this.props.row}${this.props.num}`
        };

        this.seatPurchaseChild = this.seatPurchaseChild.bind(this);
        this.seatPurchaseAdult = this.seatPurchaseAdult.bind(this);
        this.tempDis = this.tempDis.bind(this);

    }
    componentDidMount() {
        if (this.props.data !== "0") {
            this.setState({
                colour: "danger",
                disabled: true, 
            })
        }
        else {
            this.setState({
                colour: "success",
                disabled: false,
                active: false
            })
        }
    }
    seatPurchaseChild() {
            this.setState({ colour: "primary" })
            this.setState({active:false})
            this.props.chi(this.state.name);

        
    }
    seatPurchaseAdult(){
        
            this.setState({ colour: "primary" })
            this.setState({active:false})
            this.props.adult(this.state.name);
    }
    tempDis() {
        if(this.state.colour === "primary"){
            this.setState({color:"success"})
            this.props.delete()
        }
    }
    render() {
        return (
            <Dropdown size="sm" direction="right" isOpen={this.state.btnDropright} toggle={() => { this.setState({ btnDropright: !this.state.btnDropright }); }}>
            <DropdownToggle disabled={this.state.active} color={this.state.colour} onClick={this.tempDis}>
            {this.state.name}
            </DropdownToggle>
            {/* <DropdownMenu disabled={this.state.disabled}>
                <DropdownItem onClick={this.seatPurchaseChild}>Child</DropdownItem>
                <DropdownItem onClick={this.seatPurchaseAdult}>Adult</DropdownItem>
            </DropdownMenu> */}
            </Dropdown>
        );
    }

};

export default MyClassComponent;
