import React from 'react';

class AddMovie extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          title: '',
          synopsis: '',
          director: '',
          age_rating: '',
          starring: '',
          releaseDate: new Date(),
          duration: 0,
          writers: '',
          genre:''
        }
    }

    render() {
        return(
            <div class="container">
              <form id="newmovie">
                <div class="formgroup">
                  <label htmlFor="title">Title</label>
                  <input type="text" className="form-control" value={this.state.title} onChange={this.onTitleChange.bind(this)} required/>    
                </div>
                <div class="formgroup">
                  <label htmlFor="title">Synopsis</label>
                  <input type="text" className="form-control" value={this.state.title} onChange={this.onSynChange.bind(this)} required/>    
                </div>
              </form>
            </div>
        )
    }

    onTitleChange(event) {
        this.setState({title: event.target.value})
      }
    
    onSynChange(event) {
        this.setState({title: event.target.value})
      }
}
export default AddMovie
